import "./App.css";

function NewComponent({name,img,price,setTotal}) {

  const calculate = () => {
    setTotal((e)=>{
      e+price
    });
  };

  return (
    <>
      <div className="card atanagildo">
          <h2>King {name}</h2>
          <img src={img} alt="" />
          <p>Price: ${price}</p>
          <button onClick={calculate}>Buy</button>
        </div>
    </>
  );
}

export default NewComponent;
