import "./App.css";
import {useState} from 'react'

function NewComponent(name,img,price) {



  const [total, setTotal] = useState(0);
  const calculate = () => {
    setTotal(128 + total);
  };

  return (
    <>
      <div className="card atanagildo">
          <h2>King {name}</h2>
          <img src={img} alt="" />
          <p>Price: ${price}</p>
          <button onClick={calculate}>Buy</button>
        </div>
    </>
  );
}

export default NewComponent;
